def obtener_parametros_modelo(tecnica):
  if tecnica == "redes":
    layer_sizes_1 = [(i,) for i in range(1, 21)]
    layer_sizes_2 = [(i, j) for i in range(1, 21) for j in range(1, 21)]
    layer_sizes_3 = [(i, j, k) for i in range(1, 21) for j in range(1, 21) for k in range(1, 21)]

    return {
      'hidden_layer_sizes': layer_sizes_1 + layer_sizes_2 + layer_sizes_3,
      'activation': ['tanh'],
      'solver': ['lbfgs'],
      'alpha': [1e-5],
    }
  elif tecnica == "arboles":
    return {
      'criterion': ['gini', 'entropy', 'log_loss'],
      'max_depth': [None, 5, 10],
      'splitter': ['best', 'random'],
    }
  else:
    raise ValueError("Tipo de modelo no soportado")

